<template>  <div class="v-table">    <div class="v-table__header">      <p class="v-table__col">Avatar</p>      <p class="v-table__col v-table__action" @click="sortNumber('id')">        ID        <span class="material-icons v-table__icon-sort">sort</span>      </p>      <p class="v-table__col v-table__action" @click="sortText('first_name')">        Name        <span class="material-icons v-table__icon-sort">sort</span>      </p>      <p class="v-table__col v-table__action" @click="sortText('last_name')">        Last name        <span class="material-icons v-table__icon-sort">sort</span>      </p>      <p class="v-table__col">Phone</p>      <p class="v-table__col v-table__action" @click="sortText('gender')">        Gender        <span class="material-icons v-table__icon-sort">sort</span>      </p>      <p class="v-table__col v-table__action" @click="sortDate('date_of_birth')">        Date of birth        <span class="material-icons v-table__icon-sort">sort</span></p>    </div>    <div class="v-table__body">      <v-table-row        v-for="row in paginatedUsers"        :key="row.uid"        :row-data="row"      />  </div>    <div v-if="showPagination" class="v-table__pagination">        <button class="v-table__page"             v-for="page in pages"             :key="page"             @click="pageClick(page)"              :class="{'v-table__page_selected': page === pageNumber }"        >          {{page}}        </button>    </div>  </div></template><script>import VTableRow from "./v-table-row";export default {  name: "v-table",  components: {VTableRow},  props: {    tableData: {      type: Array,      default: () => []    },    showPagination: {      type: Boolean,      default: false    },    showScroll: {      type: Boolean,      default: false    }  },  data(){    return {      tablePerPage: 5,      pageNumber: 1,      sortedProp: null,      sortKey: "",    }  },  computed: {    pages() {      return Math.ceil(this.tableData.length / this.tablePerPage)    },    paginatedUsers(){      let from = (this.pageNumber - 1) * this.tablePerPage;      let to = from + this.tablePerPage;      return this.tableData.slice(from, to);    }  },  methods: {    pageClick(page){      this.pageNumber = page    },    sortText(field){      this.tableData.sort((a,b)=> a[field].localeCompare(b[field]))    },    sortNumber(field){      this.tableData.sort((a,b)=> a[field]-b[field])    },    sortDate(field){      this.tableData.sort((a,b)=> a[field].localeCompare(b[field]))    },  }}</script><style>  .v-table{    max-width: 900px;    margin: 0 auto;  }  .v-table__header {    display: grid;    grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));    border-bottom: 1px solid #ccc;  }  .v-table__col {    padding: 10px;    display: flex;    align-items: center;    gap: 5px;    justify-content: center;    box-shadow: 0 0 0 1px #ccc;  }  .v-table__pagination {    border-top: 1px solid #cccccc;    padding-top: 20px;    margin-top: 20px;    display: flex;    justify-content: center;    align-items: center;    gap: 10px;  }  .v-table__page {    padding: 10px;    border: 1px solid #cccccc;    background-color: transparent;    cursor: pointer;  }  .v-table__page:hover {    background-color: #cccccc;  }  .v-table__page_selected {    background-color: antiquewhite;  }  .v-table__action {    cursor: pointer;  }</style>